# GeographyDivisionSelect
module ActionView
  module Helpers
    class InstanceTag  
      def to_geography_division_select_tag(options, html_options)
        html_options = html_options.stringify_keys
        add_default_name_and_id(html_options)
        get_value = value(object)
        options[:selected] = options.has_key?(:selected) ? options[:selected] : get_value
        content_tag("select", 
          add_options(
            geography_division_options_for_select(options), options, get_value
          ), html_options
        )
      end
    end
    class FormBuilder
      def geography_division_select(method, options = {}, html_options = {})
        @template.geography_division_select(@object_name, method, options.merge(:object => @object), html_options)
      end
    end 
    
    module FormOptionsHelper
        # Return select and the options tags based on conditions applied
        # Allowed Types
        # Region - To print the Regions i.e. Continents of the World. Supported are Africa, Asia, Australia, Europe, North America, South America
        # Country - To print All the Countries of the World. Currently we have supported all the 237 countries in the world.
        # State - To print All the states of a particular or a set of countries.
            
        def geography_division_select(object, method, options = {}, html_options = {})
          InstanceTag.new(object, method, self, options.delete(:object)).to_geography_division_select_tag(options, html_options)
        end
        
        def geography_division_options_for_select(options={})
          type = options[:drilldown].nil? ? "region" : options[:drilldown].downcase
          onlyconditions  = options[:only]
          excludeconditions = options[:exclude]
          selected_options = ""
          selected=options[:selected]
          sel_region_options = Array.new
          prioritylist = options[:priority]
          case type
            when "region"
              #calculate constraints for region
              sel_region_options = get_regions(onlyconditions, excludeconditions)      
              if prioritylist
                selected_options+= options_for_select(prioritylist.split(","),selected)
                selected_options+="<option value='' disabled='disabled'>________________</option>"
              end
              selected_options+= options_for_select(sel_region_options.sort, selected)
            when "country"
              sel_country_options = get_countries(onlyconditions,excludeconditions)
              if prioritylist
                selected_options+= options_for_select(prioritylist.split(","),selected)
                selected_options+="<option value='' disabled='disabled'>________________</option>"
              end
              selected_options+= options_for_select(sel_country_options.sort, selected)
            when "state"
              sel_state_options = get_states(onlyconditions,excludeconditions)
              if prioritylist
                selected_options+= options_for_select(prioritylist.split(","),selected)
                selected_options+="<option value='' disabled='disabled'>________________</option>"
              end
              selected_options+= options_for_select(sel_state_options.sort, selected)
          end
          return selected_options
        end
       
