      #Gets all the regions based on the conditions supplied in the options
      def get_regions(onlyconditions,excludeconditions)
            sel_region_options = GEOGRAPHY_DIVISION_REGION
              if !onlyconditions.nil? && onlyconditions[:region]
                sel_region_options = onlyconditions[:region].split(",")
              end
              if !excludeconditions.nil? && excludeconditions[:region]
                sel_region_options = GEOGRAPHY_DIVISION_REGION - excludeconditions[:region].split(",")
              end
            return sel_region_options  
        end

      #Gets all the Countries based on the conditions supplied in the options
      def get_countries(onlyconditions, excludeconditions)
           sel_region_options = get_regions(onlyconditions, excludeconditions)      
           sel_countries = Array.new
           for region in sel_region_options
             region = region.gsub(" ","")
             begin
              getcol = eval("GEOGRAPHY_DIVISION_#{region.upcase}_COUNTRIES")
              unless (getcol.nil? || getcol.empty?)
               getcol.each{|country| sel_countries << country}
              end
             rescue
             #do nothing and go to next one
             end
           end
           if !onlyconditions.nil? && onlyconditions[:country]
            sel_countries = onlyconditions[:country].split(",")
           end 
           if !excludeconditions.nil? && excludeconditions[:country]
            sel_countries = sel_countries - excludeconditions[:country].split(",")
           end
           return sel_countries
      end
         
         #Gets all the states based on the conditions supplied in the options
      def get_states(onlyconditions, excludeconditions)
          sel_country_options = get_countries(onlyconditions,excludeconditions)
          sel_states = Array.new
          for country in sel_country_options
           country = country.gsub(" ","")
           country = country.gsub(",","")
           country = country.gsub("'","")
           begin
            getcol = eval("GEOGRAPHY_DIVISION_#{country.upcase}_STATES")
            unless (getcol.nil? || getcol.empty?)
              getcol.each{|state| sel_states << state}
            end
           rescue
             #do nothing and go to next one
             end
           end
           if !onlyconditions.nil? && onlyconditions[:state]
            sel_states = onlyconditions[:state].split(",")
           end 
           if !excludeconditions.nil? && excludeconditions[:state]
            sel_states = sel_states - excludeconditions[:state].split(",")
          end
           return sel_states
        end
    end
  end
